function TaskCard({task}){
    return (
        <div >
        <h1>{task.title}</h1>
        <div>
            <button>delete</button>
            <button>edit</button>
        </div>
        <p className="text-slate-300">{task.description}</p>
        <p>
        {task.date &&
          new Date(task.date).toLocaleDateString("en-US", {
            weekday: "long",
            year: "numeric",
            month: "long",
            day: "numeric",
          })}
        </p>
        </div>
        

    );
}
export default TaskCard;
