import React, { useState, useEffect } from "react";
import "./styles/SearchPage.css"; // Importa tus estilos CSS
import '../App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import {NavBar} from '../components/NavBar';
import {Footer} from '../components/Footer'
import Slider from "react-slick";


function SearchPage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [planets, setPlanets] = useState([]);
  const [planetImages, setPlanetImages] = useState({});


  useEffect(() => {
    async function fetchPlanets() {
      const response = await fetch('https://api.le-systeme-solaire.net/rest/bodies?filter[]=isPlanet,eq,true');
      const data = await response.json();
      const planetsData = data.bodies;
      setPlanets(planetsData);

      const imageFetchPromises = planetsData.map(planet =>
        fetch(`https://images-api.nasa.gov/search?q=${planet.englishName}&media_type=image`).then(response => response.json())
      );

      Promise.all(imageFetchPromises).then(imageResponses => {
        const newPlanetImages = {};
        imageResponses.forEach((imageData, index) => {
          const planetName = planetsData[index].englishName;
          if (imageData.collection && imageData.collection.items.length > 0) {
            newPlanetImages[planetName] = imageData.collection.items[0].links[0].href;
          }
        });
        setPlanetImages(newPlanetImages);
      });
    }

    fetchPlanets();
  }, []);

  useEffect(() => {
    planets.forEach(planet => {
      fetch(`https://images-api.nasa.gov/search?q=${planet.name}&media_type=image`)
        .then(response => response.json())
        .then(data => {
          // Almacenar las imágenes en el estado, asociadas al nombre del planeta
          setPlanetImages(prevImages => ({
            ...prevImages,
            [planet.name]: data.collection.items
          }));
        })
        .catch(error => console.error(`Error fetching images for planet ${planet.name}:`, error));
    });
  }, [planets]);


  useEffect(() => {
    fetch('https://api.le-systeme-solaire.net/rest/bodies?filter[]=isPlanet,eq,true')
      .then(response => response.json())
      .then(data => setPlanets(data.bodies));
  }, []);


  useEffect(() => {
    // Función para buscar imágenes en la NASA API
    const searchImages = async () => {
      try {
        const response = await fetch(
          `https://images-api.nasa.gov/search?q=${searchTerm}&media_type=image`
        );
        const data = await response.json();
        setSearchResults(data.collection.items);
      } catch (error) {
        console.error("Error al buscar imágenes:", error);
      }
    };

    // Realizar la búsqueda cuando cambia el término de búsqueda
    if (searchTerm.trim() !== "") {
      searchImages();
    }
  }, [searchTerm]);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const handleImageClose = () => {
    setSelectedImage(null);
  };

  const clearImages = () => {
    setSearchResults([]);
    
  };


   // Configuración del carrusel
   const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      }
    ]
  };

  return (
    
    <div>
         <NavBar />
      <div className="search-bar">
        <input
          type="text"
          placeholder="Buscar imágenes..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="button" onClick={clearImages} className="clear-button">
          Limpiar
        </button>
      </div>
      <div className="image-grid">
        {searchResults.map((image, index) => (
          <div
            key={index}
            className="image-card"
            onClick={() => handleImageClick(image)}
          >
            <div className="image-target">
              <img src={image.links[0].href} alt={image.data[0].title} />
              <div className="image-info">
                <p>{image.data[0].title}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
      {selectedImage && (
        <div className="image-popup" onClick={handleImageClose}>
          <div className="image-popup-content">
            <img
              src={selectedImage.links[0].href}
              alt={selectedImage.data[0].title}
            />
            <div className="image-info">
              <p>{selectedImage.data[0].title}</p>
              <p>Fecha: {selectedImage.data[0].date_created}</p>
              <p>Creador: {selectedImage.data[0].center}</p>
              <p>Descripción: {selectedImage.data[0].description}</p>
            </div>
          </div>
        </div>
      )}
       {/* Carrusel de planetas */}
       <div className="planet-carousel-container">
        <Slider {...settings}>
          {planets.map((planet, index) => {
            const imageSrc = planetImages[planet.englishName];
            return (
              <div key={index} className="planet-slide">
                <h3>{planet.englishName}</h3>
                {imageSrc && <img src={imageSrc} alt={planet.englishName} />}
                {/* Agrega aquí más información y estilos según sea necesario */}
              </div>
            );
          })}
        </Slider>
      </div>
    </div>

  );
}

export default SearchPage;
