import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles/DashboardPage.css';
import {NavBar} from '../components/NavBar';
import {useForm} from 'react-hook-form'
import { getUsers, addUser, deleteUser, updateUser } from '../api/api.js';

function DashboardPage() {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ username: '', password: '' });
  const [editUser, setEditUser] = useState({ id: null, username: '', password: '' });

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleAddUser = async () => {
    try {
      await axios.post('http://localhost:4000/api/users', newUser);
      alert('Usuario agregado correctamente');
      fetchUsers();
      setNewUser({ username: '', password: '' });
    } catch (error) {
      console.error('Error al agregar usuario:', error);
      alert('Error al agregar usuario');
    }
  };

  const handleDelete = async (id) => {
    console.log('Deleting user with id:', id);
    try {
      await axios.delete(`http://localhost:4000/api/users/${id}`);
      alert('Usuario eliminado correctamente');
      fetchUsers();
    } catch (error) {
      console.error('Error al eliminar usuario:', error);
      alert('Error al eliminar usuario');
    }
  };

  const handleEditUser = (user) => {
    setEditUser({ id: user.id, username: user.username, password: user.password });
  };

  const handleUpdateUser = async () => {
    console.log('Updating user with id:', editUser.id);
    try {
      await axios.patch(`http://localhost:4000/api/users/${editUser.id}`, editUser);
      alert('Usuario actualizado correctamente');
      fetchUsers();
      setEditUser({ id: null, username: '', password: '' });
    } catch (error) {
      console.error('Error al actualizar usuario:', error);
      alert('Error al actualizar tu usuario');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const {register, handleSubmit} = useForm()
  const onSubmit = handleSubmit((data) => {
    console.log(data)
  })

  return (
    
  <div>
    <NavBar />
     <div className="dashboard-container">
      
      <div className="dashboard-card">
        <h1 className="dashboard-title">Mi Dashboard Personalizado</h1>
        <div className="form-container">
          <input
            type="text"
            placeholder="Nombre de usuario"
            value={newUser.username}
            onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
            className="input-field"
          />
          <input
            type="password"
            placeholder="Contrase침a"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            className="input-field"
          />
          <button onClick={handleAddUser} className="action-button">
            Agregar Usuario
          </button>
        </div>
        <table className="user-table">
          <thead>
            <tr>
              <th>Usuario</th>
              <th>Contrase침a</th>
              <th>Fecha de creaci칩n</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.username}</td>
                <td>{user.password}</td>
                <td>{user.createdAt}</td>
                <td>
                  <button onClick={() => handleDelete(user.id)} className="delete-button">
                    Eliminar
                  </button>
                  <button onClick={() => handleEditUser(user)} className="edit-button">
                    Editar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {editUser.id !== null && (
          <div className="edit-form">
            <h2>Editar Usuario</h2>
            <input
              type="text"
              placeholder="Nombre de usuario"
              value={editUser.username}
              onChange={(e) => setEditUser({ ...editUser, username: e.target.value })}
              className="input-field"
            />
            <input
              type="password"
              placeholder="Contrase침a"
              value={editUser.password}
              onChange={(e) => setEditUser({ ...editUser, password: e.target.value })}
              className="input-field"
            />
            <button onClick={handleUpdateUser} className="update-button">
              Actualizar Usuario
            </button>
          </div>
        )}
      </div>
     </div>
        <div className='custom-container'>
          <form onSubmit={onSubmit}>
            <input 
              type="text" 
              placeholder='Title' 
              {...register('title')}
              className='custom-input'
              autoFocus
            />
            <textarea 
              rows="3" 
              placeholder='Description'
              {...register("description")}
              className='custom-textarea'
            >
            </textarea>
            <button className='custom-button'>
              Save
            </button>
          </form>
        </div>
  </div>
  );
}

export default DashboardPage;
