import React, { useEffect, useState } from 'react';
import axios from 'axios';

function DashboardPage() {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ username: '', password: '' });
  const [editUser, setEditUser] = useState({ id: null, username: '', password: '' });

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:4000/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleAddUser = async () => {
    try {
      await axios.post('http://localhost:4000/users', newUser);
      alert('Usuario agregado correctamente');
      fetchUsers();
      setNewUser({ username: '', password: '' });
    } catch (error) {
      alert('Error al agregar usuario');
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/users/${id}`);
      alert('Usuario eliminado correctamente');
      fetchUsers();
    } catch (error) {
      alert('Error al eliminar usuario');
    }
  };

  const handleEditUser = (user) => {
    setEditUser({ id: user.id, username: user.username, password: user.password });
  };

  const handleUpdateUser = async () => {
    try {
      await axios.patch(`http://localhost:4000/users/${editUser.id}`, editUser);
      alert('Usuario actualizado correctamente');
      fetchUsers();
      setEditUser({ id: null, username: '', password: '' });
    } catch (error) {
      alert('Error al actualizar tu usuario');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="bg-gray-800 text-white min-h-screen p-4">
      <div className="bg-gray-700 rounded-lg shadow-lg p-4">
        <h1 className="text-2xl font-semibold mb-4">Mi Dashboard Personalizado</h1>
        <div className="flex space-x-4 mb-4">
          <input
            type="text"
            placeholder="Nombre de usuario"
            value={newUser.username}
            onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
            className="w-1/2 bg-gray-600 text-white p-2 rounded"
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={newUser.password}
            onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
            className="w-1/2 bg-gray-600 text-white p-2 rounded"
          />
          <button onClick={handleAddUser} className="bg-blue-500 text-white px-4 py-2 rounded">
            Agregar Usuario
          </button>
        </div>
        <table className="w-full table-auto">
          <thead>
            <tr>
              <th className="px-4 py-2">Usuario</th>
              <th className="px-4 py-2">Contraseña</th>
              <th className="px-4 py-2">Fecha de creación</th>
              <th className="px-4 py-2">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td className="px-4 py-2">{user.username}</td>
                <td className="px-4 py-2">{user.password}</td>
                <td className="px-4 py-2">{user.createdAT}</td>
                <td className="px-4 py-2">
                  <button
                    onClick={() => handleDelete(user.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded mr-2"
                  >
                    Eliminar
                  </button>
                  <button
                    onClick={() => handleEditUser(user)}
                    className="bg-blue-500 text-white px-2 py-1 rounded"
                  >
                    Editar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {editUser.id !== null && (
          <div className="mt-4">
            <h2 className="text-lg font-semibold">Editar Usuario</h2>
            <input
              type="text"
              placeholder="Nombre de usuario"
              value={editUser.username}
              onChange={(e) => setEditUser({ ...editUser, username: e.target.value })}
              className="w-1/2 bg-gray-600 text-white p-2 rounded mt-2"
            />
            <input
              type="password"
              placeholder="Contraseña"
              value={editUser.password}
              onChange={(e) => setEditUser({ ...editUser, password: e.target.value })}
              className="w-1/2 bg-gray-600 text-white p-2 rounded mt-2"
            />
            <button onClick={handleUpdateUser} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
              Actualizar Usuario
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default DashboardPage;
