import React, { useState, useEffect } from "react";
import "./styles/SearchPage.css"; // Importa tus estilos CSS

function SearchPage () {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    // Función para buscar imágenes en la NASA API
    const searchImages = async () => {
      try {
        const apiKey = "cR3FgKzsP0hthiWIsTef8tdCfzawMb86cjuLjh4J"; // Reemplaza con tu API Key
        const response = await fetch(
          `https://images-api.nasa.gov/search?q=${searchTerm}&media_type=image&api_key=${apiKey}`
        );
        const data = await response.json();
        setSearchResults(data.collection.items);
      } catch (error) {
        console.error("Error al buscar imágenes:", error);
      }
    };

    // Realizar la búsqueda cuando cambia el término de búsqueda
    if (searchTerm.trim() !== "") {
      searchImages();
    }
  }, [searchTerm]);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const handleImageClose = () => {
    setSelectedImage(null);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Buscar imágenes..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <div className="image-grid">
        {searchResults.map((image, index) => (
          <div key={index} className="image-card" onClick={() => handleImageClick(image)}>
            <img src={image.links[0].href} alt={image.data[0].title} />
          </div>
        ))}
      </div>
      {selectedImage && (
        <div className="image-popup" onClick={handleImageClose}>
          <div className="image-popup-content">
            <img src={selectedImage.links[0].href} alt={selectedImage.data[0].title} />
            <p>{selectedImage.data[0].description}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchPage;
