import {createContext, useState, useContext} from 'react'
import {registerRequest} from '../api/auth'

export const AuthContext = createContext ()
const useAuth = ()  => {
    const context = useContext(AuthContext)
    if(!context) {
        throw new Error("useAuth must be used within an AuthProvider");
    }
    return context;
}


export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
  
    const signup = async (values) => {
      if (typeof values === 'undefined') {
        // Puedes manejar el caso en el que 'values' no está definido de la manera que prefieras.
        // Por ejemplo, podrías lanzar un error o mostrar un mensaje de error.
        // Por ahora, solo imprimiremos un mensaje de advertencia en la consola.
        console.warn("'values' no está definido en la función signup.");
        return;
      }
  
      const res = await registerRequest(values);
      console.log(res.data);
      setUser(res.data);
    };
  
    return (
      <AuthContext.Provider value={{
        signup,
        user,
      }}>
        {children}
      </AuthContext.Provider>
    );
  };
